      }

      .cart--link {
        align-items: center;
        display: flex;
        margin-left: 0rem;

        .cart--image {
          width: 25px;
          min-width: 25px;
          height: 21px;
        }
        .cart--item--counter {
          font-weight: 400;
          line-height: 1.7;
          font-size: 0.6875rem;
          font-size: 0.8125rem;
          margin-left: 1px;
          margin-bottom: 0.75rem;
          @media screen and (min-width: 992px) {
            line-height: 1.5;
          }
        }
      }

      .language--select--wrapper {
        height: fit-content;
        width: 84px;
        min-width: 84px;
        padding: 0rem;
        position: relative;
        top: initial;
        right: initial;
        left: initial;
        z-index: initial;
        margin-left: 1.5rem;

        .language--select {
          width: 100%;
          outline: 0;
          position: relative;
          appearance: none;
          transition: all 0.2s;
          padding-top: 2px;
          padding-bottom: 1px;
          line-height: normal;
          color: currentColor;
          height: 24px;
          font-size: 0.6875rem;
          padding-left: 0.5rem;
          padding-right: 0.5rem;
          border: 1px solid #cdd1ce;
          border-radius: 0;
          background: transparent;
          border-color: #acafad;
          padding-right: 2rem;

          @media screen and (min-width: 992px) {
            height: 32px;
            font-size: 0.8125rem;
          }
        }
        .language--select--dropdown {
          color: currentColor;
          font-size: 1.25rem;
          position: absolute;
          display: inline-flex;
          width: 1.5rem;
          height: 100%;
          align-items: center;
          justify-content: center;
          right: 0.5rem;
          pointer-events: none;
          top: 50%;
          transform: translateY(-50%);
        }
      }
    }
  }
`;
export default Header;
